import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
} from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { Icon } from '@rneui/themed';
import {
  fetchBookingsStart,
  fetchBookingsSuccess,
  fetchBookingsFailure,
  setCurrentBooking,
} from '../redux/slices/bookingsSlice';
import { mockApi } from '../api/apiService';

const BookingHistoryScreen = ({ navigation }) => {
  const dispatch = useDispatch();
  const { bookings, loading, error } = useSelector((state) => state.bookings);
  const [activeTab, setActiveTab] = useState('upcoming');

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      dispatch(fetchBookingsStart());
      const data = await mockApi.getBookings();
      dispatch(fetchBookingsSuccess(data));
    } catch (error) {
      dispatch(fetchBookingsFailure(error.message || 'Failed to fetch bookings'));
    }
  };

  const handleBookingPress = (booking) => {
    dispatch(setCurrentBooking(booking));
    navigation.navigate('BookingDetails', { booking });
  };

  const getFilteredBookings = () => {
    if (!bookings || bookings.length === 0) return [];

    const today = new Date();

    if (activeTab === 'upcoming') {
      return bookings.filter((booking) => {
        const checkOutDate = new Date(booking.checkOut);
        return checkOutDate >= today && booking.status !== 'cancelled';
      });
    } else if (activeTab === 'past') {
      return bookings.filter((booking) => {
        const checkOutDate = new Date(booking.checkOut);
        return checkOutDate < today && booking.status !== 'cancelled';
      });
    } else if (activeTab === 'cancelled') {
      return bookings.filter((booking) => booking.status === 'cancelled');
    }

    return bookings;
  };

  const renderBookingItem = ({ item }) => {
    const checkInDate = new Date(item.checkIn);
    const checkOutDate = new Date(item.checkOut);

    // Calculate number of nights
    const diffTime = checkOutDate.getTime() - checkInDate.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    // Format dates
    const formatDate = (date) => {
      const options = { month: 'short', day: 'numeric', year: 'numeric' };
      return date.toLocaleDateString('en-US', options);
    };

    return (
      <TouchableOpacity
        style={styles.bookingCard}
        onPress={() => handleBookingPress(item)}
      >
        <View style={styles.bookingHeader}>
          <Text style={styles.hotelName}>{item.hotelName}</Text>
          <View style={[
            styles.statusTag,
            item.status === 'confirmed' ? styles.confirmedTag : 
            item.status === 'pending' ? styles.pendingTag : styles.cancelledTag
          ]}>
            <Text style={[
              styles.statusText,
              item.status === 'confirmed' ? styles.confirmedText : 
              item.status === 'pending' ? styles.pendingText : styles.cancelledText
            ]}>
              {item.status.charAt(0).toUpperCase() + item.status.slice(1)}
            </Text>
          </View>
        </View>

        <View style={styles.bookingDetails}>
          <View style={styles.detailItem}>
            <Icon name="calendar" type="ionicon" size={16} color="#666" />
            <Text style={styles.detailText}>
              {formatDate(checkInDate)} - {formatDate(checkOutDate)}
            </Text>
          </View>
          <View style={styles.detailItem}>
            <Icon name="moon" type="ionicon" size={16} color="#666" />
            <Text style={styles.detailText}>{diffDays} {diffDays === 1 ? 'night' : 'nights'}</Text>
          </View>
          <View style={styles.detailItem}>
            <Icon name="people" type="ionicon" size={16} color="#666" />
            <Text style={styles.detailText}>{item.guests} {item.guests === 1 ? 'guest' : 'guests'}</Text>
          </View>
          <View style={styles.detailItem}>
            <Icon name="bed" type="ionicon" size={16} color="#666" />
            <Text style={styles.detailText}>{item.roomType} Room</Text>
          </View>
        </View>

        <View style={styles.bookingFooter}>
          <Text style={styles.priceText}>${item.totalPrice}</Text>
          <View style={styles.viewDetailsContainer}>
            <Text style={styles.viewDetailsText}>View Details</Text>
            <Icon name="chevron-forward" type="ionicon" size={16} color="#007BFF" />
          </View>
        </View>
      </TouchableOpacity>
    );
  };

  const renderEmptyList = () => (
    <View style={styles.emptyContainer}>
      <Icon name="calendar-outline" type="ionicon" size={80} color="#CCC" />
      <Text style={styles.emptyTitle}>No bookings found</Text>
      <Text style={styles.emptyText}>
        {activeTab === 'upcoming'
          ? "You don't have any upcoming bookings."
          : activeTab === 'past'
          ? "You don't have any past bookings."
          : "You don't have any cancelled bookings."}
      </Text>
      <TouchableOpacity
        style={styles.exploreButton}
        onPress={() => navigation.navigate('Home')}
      >
        <Text style={styles.exploreButtonText}>Explore Hotels</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>My Bookings</Text>
      </View>

      <View style={styles.tabContainer}>
        <TouchableOpacity
          style={[styles.tab, activeTab === 'upcoming' && styles.activeTab]}
          onPress={() => setActiveTab('upcoming')}
        >
          <Text
            style={[
              styles.tabText,
              activeTab === 'upcoming' && styles.activeTabText,
            ]}
          >
            Upcoming
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tab, activeTab === 'past' && styles.activeTab]}
          onPress={() => setActiveTab('past')}
        >
          <Text
            style={[
              styles.tabText,
              activeTab === 'past' && styles.activeTabText,
            ]}
          >
            Past
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tab, activeTab === 'cancelled' && styles.activeTab]}
          onPress={() => setActiveTab('cancelled')}
        >
          <Text
            style={[
              styles.tabText,
              activeTab === 'cancelled' && styles.activeTabText,
            ]}
          >
            Cancelled
          </Text>
        </TouchableOpacity>
      </View>

      {loading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#007BFF" />
        </View>
      ) : error ? (
        <View style={styles.errorContainer}>
          <Icon name="alert-circle" type="ionicon" size={60} color="#FF3B30" />
          <Text style={styles.errorText}>{error}</Text>
          <TouchableOpacity
            style={styles.retryButton}
            onPress={fetchBookings}
          >
            <Text style={styles.retryButtonText}>Retry</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <FlatList
          data={getFilteredBookings()}
          renderItem={renderBookingItem}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.listContainer}
          showsVerticalScrollIndicator={false}
          ListEmptyComponent={renderEmptyList}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
