import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TextInput,
  TouchableOpacity,
  Alert,
  KeyboardAvoidingView,
  Platform,
  Modal,
} from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { Icon } from '@rneui/themed';
import { createBookingStart, createBookingSuccess, createBookingFailure } from '../redux/slices/bookingsSlice';
import apiService from '../api/apiService';
import AsyncStorage from '@react-native-async-storage/async-storage';
import DateTimePicker from '@react-native-community/datetimepicker';

// Date picker component
const DatePicker = ({ label, value, onChange }) => {
  const [showPicker, setShowPicker] = useState(false);
  const [selectedDate, setSelectedDate] = useState(value ? new Date(value) : new Date());

  const handleDateChange = (event, date) => {
    if (date) {
      setSelectedDate(date);
      onChange(date.toISOString().split('T')[0]);
    }
    setShowPicker(false);
  };

  return (
    <View style={styles.datePickerContainer}>
      <Text style={styles.datePickerLabel}>{label}</Text>
      <TouchableOpacity
        style={styles.datePickerValue}
        onPress={() => setShowPicker(true)}
      >
        <Text style={styles.dateText}>{value || 'Select date'}</Text>
        <Icon name="calendar" type="ionicon" size={20} color="#666" />
      </TouchableOpacity>
      {Platform.OS === 'ios' ? (
        <Modal
          visible={showPicker}
          transparent={true}
          animationType="slide"
        >
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <DateTimePicker
                value={selectedDate}
                mode="date"
                display="spinner"
                onChange={handleDateChange}
                minimumDate={new Date()}
              />
              <TouchableOpacity
                style={styles.modalButton}
                onPress={() => setShowPicker(false)}
              >
                <Text style={styles.modalButtonText}>Done</Text>
              </TouchableOpacity>
            </View>
          </View>
        </Modal>
      ) : (
        showPicker && (
          <DateTimePicker
            value={selectedDate}
            mode="date"
            display="default"
            onChange={handleDateChange}
            minimumDate={new Date()}
          />
        )
      )}
    </View>
  );
};

const BookingScreen = ({ navigation, route }) => {
  const { hotelId, hotelName, roomId, roomType, price } = route.params;
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.auth);
  const { loading } = useSelector((state) => state.bookings);
  
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [guests, setGuests] = useState('1');
  const [specialRequests, setSpecialRequests] = useState('');
  const [contactName, setContactName] = useState(user?.name || '');
  const [contactEmail, setContactEmail] = useState(user?.email || '');
  const [contactPhone, setContactPhone] = useState('');
  
  // Calculate total nights and price
  const calculateNights = () => {
    if (!checkIn || !checkOut) return 0;
    
    const checkInDate = new Date(checkIn);
    const checkOutDate = new Date(checkOut);
    const diffTime = checkOutDate.getTime() - checkInDate.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    return diffDays > 0 ? diffDays : 0;
  };
  
  const nights = calculateNights();
  const totalPrice = nights * price;
  
  const validateForm = () => {
    if (!checkIn || !checkOut) {
      Alert.alert('Error', 'Please select check-in and check-out dates');
      return false;
    }
    
    if (nights <= 0) {
      Alert.alert('Error', 'Check-out date must be after check-in date');
      return false;
    }
    
    if (!contactName || !contactEmail || !contactPhone) {
      Alert.alert('Error', 'Please fill in all contact information');
      return false;
    }
    
    // Simple email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(contactEmail)) {
      Alert.alert('Error', 'Please enter a valid email address');
      return false;
    }
    
    return true;
  };
  
  const handleBookNow = () => {
    if (!checkIn || !checkOut) {
      Alert.alert('Error', 'Please select check-in and check-out dates');
      return;
    }

    if (guests < 1) {
      Alert.alert('Error', 'Please select at least one guest');
      return;
    }

    const bookingDetails = {
      checkIn,
      checkOut,
      guests,
      specialRequests,
    };

    navigation.navigate('Payment', {
      bookingDetails,
      hotel: {
        id: hotelId,
        name: hotelName,
      },
      room: {
        id: roomId,
        type: roomType,
        price: price,
      },
    });
  };
  
  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      style={styles.container}
    >
      <ScrollView showsVerticalScrollIndicator={false}>
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <Icon name="arrow-back" type="ionicon" size={24} color="#333" />
          </TouchableOpacity>
          <Text style={styles.headerTitle}>Book Your Stay</Text>
          <View style={styles.placeholder} />
        </View>
        
        <View style={styles.bookingDetails}>
          <Text style={styles.hotelName}>{hotelName}</Text>
          <Text style={styles.roomType}>{roomType} Room</Text>
          <Text style={styles.price}>${price} / night</Text>
        </View>
        
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Dates</Text>
          <View style={styles.datesContainer}>
            <DatePicker
              label="Check-in"
              value={checkIn}
              onChange={setCheckIn}
            />
            <DatePicker
              label="Check-out"
              value={checkOut}
              onChange={setCheckOut}
            />
          </View>
        </View>
        
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Guests</Text>
          <View style={styles.guestsContainer}>
            <TouchableOpacity
              style={styles.guestButton}
              onPress={() => setGuests(Math.max(1, parseInt(guests) - 1).toString())}
            >
              <Icon name="remove" type="ionicon" size={20} color="#333" />
            </TouchableOpacity>
            <Text style={styles.guestCount}>{guests}</Text>
            <TouchableOpacity
              style={styles.guestButton}
              onPress={() => setGuests((parseInt(guests) + 1).toString())}
            >
              <Icon name="add" type="ionicon" size={20} color="#333" />
            </TouchableOpacity>
          </View>
        </View>
        
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Special Requests</Text>
          <TextInput
            style={styles.specialRequestsInput}
            placeholder="Any special requests? (optional)"
            value={specialRequests}
            onChangeText={setSpecialRequests}
            multiline
            numberOfLines={3}
          />
        </View>
        
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Contact Information</Text>
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>Full Name</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter your full name"
              value={contactName}
              onChangeText={setContactName}
            />
          </View>
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>Email</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter your email"
              value={contactEmail}
              onChangeText={setContactEmail}
              keyboardType="email-address"
              autoCapitalize="none"
            />
          </View>
          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>Phone</Text>
            <TextInput
              style={styles.input}
              placeholder="Enter your phone number"
              value={contactPhone}
              onChangeText={setContactPhone}
              keyboardType="phone-pad"
            />
          </View>
        </View>
        
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Price Summary</Text>
          <View style={styles.summaryItem}>
            <Text style={styles.summaryLabel}>Room Price</Text>
            <Text style={styles.summaryValue}>${price} x {nights} nights</Text>
          </View>
          <View style={styles.summaryItem}>
            <Text style={styles.summaryLabel}>Taxes & Fees</Text>
            <Text style={styles.summaryValue}>Included</Text>
          </View>
          <View style={styles.divider} />
          <View style={styles.summaryItem}>
            <Text style={styles.totalLabel}>Total</Text>
            <Text style={styles.totalValue}>${totalPrice}</Text>
          </View>
        </View>
        
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.bookButton}
            onPress={handleBookNow}
            disabled={loading}
          >
            {loading ? (
              <Text style={styles.bookButtonText}>Processing...</Text>
            ) : (
              <Text style={styles.bookButtonText}>Confirm Booking</Text>
            )}
          </TouchableOpacity>
          <Text style={styles.disclaimer}>
            By confirming, you agree to our terms and conditions.
          </Text>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

