import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  FlatList,
  Image,
  ActivityIndicator,
  Modal,
  ScrollView,
} from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { Icon } from '@rneui/themed';
import { updateFilters, applyFilters, selectHotel } from '../redux/slices/hotelsSlice';
import { mockApi } from '../api/apiService';

const SearchScreen = ({ navigation, route }) => {
  const dispatch = useDispatch();
  const { filteredHotels, loading, error, filters } = useSelector((state) => state.hotels);
  const [searchQuery, setSearchQuery] = useState(route.params?.initialQuery || '');
  const [showFilters, setShowFilters] = useState(false);
  const [localFilters, setLocalFilters] = useState({
    location: route.params?.initialLocation || '',
    priceRange: [0, 1000],
    amenities: [],
    guests: 1,
  });

  // Amenities options
  const amenitiesOptions = [
    'WiFi',
    'Pool',
    'Spa',
    'Gym',
    'Restaurant',
    'Beach Access',
    'Free Parking',
    'Air Conditioning',
    'Pet Friendly',
  ];

  useEffect(() => {
    if (route.params?.initialLocation) {
      handleSearch(route.params.initialLocation);
    } else if (route.params?.initialQuery) {
      handleSearch(route.params.initialQuery);
    }
  }, [route.params]);

  const handleSearch = (query = searchQuery) => {
    const updatedFilters = {
      ...filters,
      location: query,
    };
    dispatch(updateFilters(updatedFilters));
    dispatch(applyFilters());
  };

  const handleHotelPress = (hotel) => {
    dispatch(selectHotel(hotel));
    navigation.navigate('HotelDetails', { hotelId: hotel.id });
  };

  const toggleAmenity = (amenity) => {
    setLocalFilters((prev) => {
      const amenities = [...prev.amenities];
      if (amenities.includes(amenity)) {
        return {
          ...prev,
          amenities: amenities.filter((item) => item !== amenity),
        };
      } else {
        return {
          ...prev,
          amenities: [...amenities, amenity],
        };
      }
    });
  };

  const applyLocalFilters = () => {
    dispatch(updateFilters(localFilters));
    dispatch(applyFilters());
    setShowFilters(false);
  };

  const resetFilters = () => {
    setLocalFilters({
      location: searchQuery,
      priceRange: [0, 1000],
      amenities: [],
      guests: 1,
    });
  };

  const renderHotelItem = ({ item }) => (
    <TouchableOpacity
      style={styles.hotelCard}
      onPress={() => handleHotelPress(item)}
    >
      <Image source={{ uri: item.images[0] }} style={styles.hotelImage} />
      <View style={styles.hotelInfo}>
        <Text style={styles.hotelName}>{item.name}</Text>
        <View style={styles.hotelDetails}>
          <Icon name="location" type="ionicon" size={14} color="#666" />
          <Text style={styles.hotelLocation}>{item.location}</Text>
        </View>
        <View style={styles.hotelDetails}>
          <Icon name="star" type="ionicon" size={14} color="#FFD700" />
          <Text style={styles.hotelRating}>{item.rating}</Text>
        </View>
        <View style={styles.hotelFeatures}>
          {item.amenities.slice(0, 3).map((amenity, index) => (
            <View key={index} style={styles.amenityTag}>
              <Text style={styles.amenityText}>{amenity}</Text>
            </View>
          ))}
          {item.amenities.length > 3 && (
            <View style={styles.amenityTag}>
              <Text style={styles.amenityText}>+{item.amenities.length - 3}</Text>
            </View>
          )}
        </View>
        <Text style={styles.hotelPrice}>${item.price} / night</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-back" type="ionicon" size={24} color="#333" />
        </TouchableOpacity>
        <View style={styles.searchContainer}>
          <Icon name="search" type="ionicon" size={20} color="#666" />
          <TextInput
            style={styles.searchInput}
            placeholder="Search for hotels, cities..."
            value={searchQuery}
            onChangeText={setSearchQuery}
            onSubmitEditing={() => handleSearch()}
          />
        </View>
        <TouchableOpacity 
          style={styles.filterButton}
          onPress={() => setShowFilters(true)}
        >
          <Icon name="options" type="ionicon" size={20} color="#FFF" />
        </TouchableOpacity>
      </View>

      {loading ? (
        <ActivityIndicator size="large" color="#007BFF" style={styles.loader} />
      ) : error ? (
        <Text style={styles.errorText}>{error}</Text>
      ) : filteredHotels.length === 0 ? (
        <View style={styles.noResultsContainer}>
          <Icon name="search-outline" type="ionicon" size={80} color="#CCC" />
          <Text style={styles.noResultsText}>No hotels found</Text>
          <Text style={styles.noResultsSubText}>Try adjusting your search or filters</Text>
        </View>
      ) : (
        <FlatList
          data={filteredHotels}
          renderItem={renderHotelItem}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.hotelList}
          showsVerticalScrollIndicator={false}
        />
      )}

      {/* Filter Modal */}
      <Modal
        visible={showFilters}
        animationType="slide"
        transparent={true}
        onRequestClose={() => setShowFilters(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <View style={styles.modalHeader}>
              <Text style={styles.modalTitle}>Filters</Text>
              <TouchableOpacity onPress={() => setShowFilters(false)}>
                <Icon name="close" type="ionicon" size={24} color="#333" />
              </TouchableOpacity>
            </View>

            <ScrollView showsVerticalScrollIndicator={false}>
              <View style={styles.filterSection}>
                <Text style={styles.filterTitle}>Price Range</Text>
                <View style={styles.priceRangeContainer}>
                  <Text style={styles.priceRangeText}>
                    ${localFilters.priceRange[0]} - ${localFilters.priceRange[1]}
                  </Text>
                  {/* Here you would typically add a slider component */}
                </View>
              </View>

              <View style={styles.filterSection}>
                <Text style={styles.filterTitle}>Guests</Text>
                <View style={styles.guestsContainer}>
                  <TouchableOpacity
                    style={styles.guestButton}
                    onPress={() => setLocalFilters(prev => ({
                      ...prev,
                      guests: Math.max(1, prev.guests - 1)
                    }))}
                  >
                    <Icon name="remove" type="ionicon" size={20} color="#333" />
                  </TouchableOpacity>
                  <Text style={styles.guestCount}>{localFilters.guests}</Text>
                  <TouchableOpacity
                    style={styles.guestButton}
                    onPress={() => setLocalFilters(prev => ({
                      ...prev,
                      guests: prev.guests + 1
                    }))}
                  >
                    <Icon name="add" type="ionicon" size={20} color="#333" />
                  </TouchableOpacity>
                </View>
              </View>

              <View style={styles.filterSection}>
                <Text style={styles.filterTitle}>Amenities</Text>
                <View style={styles.amenitiesContainer}>
                  {amenitiesOptions.map((amenity, index) => (
                    <TouchableOpacity
                      key={index}
                      style={[
                        styles.amenityOption,
                        localFilters.amenities.includes(amenity) && styles.amenitySelected,
                      ]}
                      onPress={() => toggleAmenity(amenity)}
                    >
                      <Text
                        style={[
                          styles.amenityOptionText,
                          localFilters.amenities.includes(amenity) && styles.amenitySelectedText,
                        ]}
                      >
                        {amenity}
                      </Text>
                    </TouchableOpacity>
                  ))}
                </View>
              </View>
            </ScrollView>

            <View style={styles.modalFooter}>
              <TouchableOpacity
                style={styles.resetButton}
                onPress={resetFilters}
              >
                <Text style={styles.resetButtonText}>Reset</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.applyButton}
                onPress={applyLocalFilters}
              >
                <Text style={styles.applyButtonText}>Apply Filters</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};

