import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  Image,
  TouchableOpacity,
  FlatList,
  Dimensions,
  ActivityIndicator,
} from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { Icon } from '@rneui/themed';
import { mockApi } from '../api/apiService';

const { width } = Dimensions.get('window');

const HotelDetailsScreen = ({ navigation, route }) => {
  const { hotelId } = route.params;
  const [hotel, setHotel] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeImageIndex, setActiveImageIndex] = useState(0);
  const [selectedRoom, setSelectedRoom] = useState(null);

  useEffect(() => {
    fetchHotelDetails();
  }, []);

  const fetchHotelDetails = async () => {
    try {
      setLoading(true);
      const data = await mockApi.getHotelById(hotelId);
      setHotel(data);
      if (data.rooms && data.rooms.length > 0) {
        setSelectedRoom(data.rooms[0]);
      }
    } catch (error) {
      setError(error.message || 'Failed to fetch hotel details');
    } finally {
      setLoading(false);
    }
  };

  const handleBookNow = () => {
    if (selectedRoom) {
      navigation.navigate('Booking', {
        hotelId: hotel.id,
        hotelName: hotel.name,
        roomId: selectedRoom.id,
        roomType: selectedRoom.type,
        price: selectedRoom.price,
      });
    }
  };

  const renderImageItem = ({ item, index }) => (
    <TouchableOpacity
      activeOpacity={0.9}
      onPress={() => setActiveImageIndex(index)}
    >
      <Image source={{ uri: item }} style={styles.carouselImage} />
    </TouchableOpacity>
  );

  const renderRoomItem = ({ item }) => (
    <TouchableOpacity
      style={[
        styles.roomCard,
        selectedRoom?.id === item.id && styles.selectedRoomCard,
      ]}
      onPress={() => setSelectedRoom(item)}
    >
      <View style={styles.roomInfo}>
        <Text style={styles.roomType}>{item.type}</Text>
        <Text style={styles.roomCapacity}>Up to {item.capacity} guests</Text>
        <View style={styles.roomFeatures}>
          <View style={styles.roomFeature}>
            <Icon name="wifi" type="ionicon" size={14} color="#666" />
            <Text style={styles.roomFeatureText}>WiFi</Text>
          </View>
          <View style={styles.roomFeature}>
            <Icon name="tv" type="ionicon" size={14} color="#666" />
            <Text style={styles.roomFeatureText}>TV</Text>
          </View>
          <View style={styles.roomFeature}>
            <Icon name="snow" type="ionicon" size={14} color="#666" />
            <Text style={styles.roomFeatureText}>AC</Text>
          </View>
        </View>
      </View>
      <View style={styles.roomPriceContainer}>
        <Text style={styles.roomPrice}>${item.price}</Text>
        <Text style={styles.perNight}>per night</Text>
        <View style={styles.availabilityTag}>
          <Text style={styles.availabilityText}>
            {item.available ? 'Available' : 'Booked'}
          </Text>
        </View>
      </View>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#007BFF" />
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.errorContainer}>
        <Icon name="alert-circle" type="ionicon" size={60} color="#FF3B30" />
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity
          style={styles.retryButton}
          onPress={fetchHotelDetails}
        >
          <Text style={styles.retryButtonText}>Retry</Text>
        </TouchableOpacity>
      </View>
    );
  }

  if (!hotel) {
    return (
      <View style={styles.errorContainer}>
        <Text style={styles.errorText}>Hotel not found</Text>
        <TouchableOpacity
          style={styles.retryButton}
          onPress={() => navigation.goBack()}
        >
          <Text style={styles.retryButtonText}>Go Back</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Image Carousel */}
        <View style={styles.carouselContainer}>
          <FlatList
            data={hotel.images}
            renderItem={renderImageItem}
            keyExtractor={(item, index) => index.toString()}
            horizontal
            pagingEnabled
            showsHorizontalScrollIndicator={false}
            onMomentumScrollEnd={(event) => {
              const newIndex = Math.floor(
                event.nativeEvent.contentOffset.x / width
              );
              setActiveImageIndex(newIndex);
            }}
          />
          <View style={styles.carouselIndicator}>
            {hotel.images.map((_, index) => (
              <View
                key={index}
                style={[
                  styles.indicatorDot,
                  index === activeImageIndex && styles.activeDot,
                ]}
              />
            ))}
          </View>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <Icon name="arrow-back" type="ionicon" size={24} color="#FFF" />
          </TouchableOpacity>
        </View>

        {/* Hotel Details */}
        <View style={styles.detailsContainer}>
          <Text style={styles.hotelName}>{hotel.name}</Text>

          <View style={styles.locationContainer}>
            <Icon name="location" type="ionicon" size={16} color="#666" />
            <Text style={styles.locationText}>{hotel.location}</Text>
          </View>

          <View style={styles.ratingContainer}>
            <Icon name="star" type="ionicon" size={16} color="#FFD700" />
            <Text style={styles.ratingText}>{hotel.rating}</Text>
            <Text style={styles.reviewsText}>(120 reviews)</Text>
          </View>

          <View style={styles.divider} />

          <Text style={styles.sectionTitle}>Description</Text>
          <Text style={styles.descriptionText}>{hotel.description}</Text>

          <View style={styles.divider} />

          <Text style={styles.sectionTitle}>Amenities</Text>
          <View style={styles.amenitiesContainer}>
            {hotel.amenities.map((amenity, index) => (
              <View key={index} style={styles.amenityItem}>
                <Icon
                  name={getAmenityIcon(amenity)}
                  type="ionicon"
                  size={18}
                  color="#007BFF"
                />
                <Text style={styles.amenityText}>{amenity}</Text>
              </View>
            ))}
          </View>

          <View style={styles.divider} />

          <Text style={styles.sectionTitle}>Select Room</Text>
          <FlatList
            data={hotel.rooms}
            renderItem={renderRoomItem}
            keyExtractor={(item) => item.id}
            horizontal={false}
            scrollEnabled={false}
          />
        </View>
      </ScrollView>

      {/* Bottom Bar */}
      <View style={styles.bottomBar}>
        <View style={styles.priceContainer}>
          <Text style={styles.priceText}>
            ${selectedRoom ? selectedRoom.price : hotel.price}
          </Text>
          <Text style={styles.priceSubtext}>per night</Text>
        </View>
        <TouchableOpacity
          style={[
            styles.bookButton,
            (!selectedRoom || !selectedRoom.available) && styles.disabledButton,
          ]}
          onPress={handleBookNow}
          disabled={!selectedRoom || !selectedRoom.available}
        >
          <Text style={styles.bookButtonText}>Book Now</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

// Helper function to get icon name for amenity
const getAmenityIcon = (amenity) => {
  const amenityIcons = {
    'WiFi': 'wifi',
    'Pool': 'water',
    'Spa': 'flower',
    'Gym': 'barbell',
    'Restaurant': 'restaurant',
    'Beach Access': 'umbrella',
    'Free Parking': 'car',
    'Air Conditioning': 'snow',
    'Pet Friendly': 'paw',
  };

  return amenityIcons[amenity] || 'checkmark-circle';
};

